import React from 'react'

const Form = () => {


    const handleSubmit = e => {
        e.preventDefault();

            props.onSubmitHandler({title , price , description})
            setIsSubmited(true)
            setTitle("")
            setPrice("")
            setDescription("")

    }



    const handleTitle = (e) => {
        setTitle(e.target.value)
        checkValidation(e.target.name, e.target.value)
    }


  return (
    <>

    <div className="text-center text-primary "><h5>Product Manager</h5></div>
    <form onSubmit={handleSubmit}>
        <p>
            <label>NAME :</label><br/>
            <input name="title" type="text" onChange={handleTitle} value={title} className={validationErrs.title? "form-control is-invalid" : "form-control"}/>
            {validationErrs.title? <p className='invalid-feedback'>{validationErrs.title}</p> : ' '}
        </p>


        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        
    </form>

    </>
  )
}

export default Form
