import React, { useState , useEffect } from 'react'
import Form from '../components/Form'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";
// import { Link } from "react-router-dom";

const UpdatePage = (props) => {
    
    const {id} = useParams();
    const history = useHistory();
    const[errors , setErrors] = useState([])
    const[success , setSuccess] = useState(false)
    const [name, setName]  = useState('')


    useEffect(() => {
        axios.get('http://localhost:8000/api/author/'+id)
        .then(res => {
            setName(res.data.Author.name); })
    }, []);

    const onSubmitHandler = newAuth => {
        setSuccess(false)
        setErrors([])
        axios.post('http://localhost:8000/api/author', newAuth)
            .then(res=>{
                setSuccess(true)
                props.addToDom(newAuth)
                history.push("/")
            })
            .catch(err=>{
                const data = err.response.data
                const errorMessages = [];
                if("errors" in data){
                    for(let field in data.errors){
                        const validationError = data.errors[field]
                        errorMessages.push(validationError.message)
                    }
                }
                setErrors(errorMessages)
            })
    }





  return (
    <>

    <div className='container mt-5'>
        <Link to="/"> HOME </Link>
        <div className='container mt-5'>
        {props.errors.map((errMsg , i) => <p key={i} className='alert alert-danger'>{errMsg} </p>)}
        {props.success && <p  className='alert alert-success'>your Favorite Author has been successafully add</p>}
        <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
        <form onSubmit={handleSubmit}>

            <p>
                <label>NAME :</label><br/>
                <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
            </p>

            <div className="d-flex justify-content-center">
            <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
            <Link to={props.linkRoute} className="btn btn-outline-secondary rounded-pill ms-2 "> Cancel </Link>
            </div>
            
        </form>

</div>
    </div>

    </>
  )
}

export default UpdatePage
