import React from 'react'

const Form = (props) => {

    const [name, setName] = useState(""); 
    const [isSubmited, setIsSubmited] = useState(false);
    const [validationErrs , setValidationErrs] = useState({});


    const handleSubmit = e => {
        setIsSubmited(false)
        e.preventDefault();

        props.onSubmitHandler({name})
        setIsSubmited(true)
        setName("")
    }



    const handleTitle = (e) => {
        setName(e.target.value)
        checkValidation(e.target.name, e.target.value)
    }


    const checkValidation = (name ,value) => {
        let errorMsg = null;
        if(value.length > 0) { 
            switch(name){
                case "title" : {
                    if(value.length < 3) {
                        errorMsg = "Title must be at least 3 Characters"
                    } 
                    break;
                }

                case "price" : {
                    if(value < 3) {
                        errorMsg = "price must be grater then 3 "
                    } 
                    break;
                }
            }
        } setValidationErrs({...validationErrs, [name] : errorMsg}) ;
    }


  return (
    <>

    <div className="text-center text-primary "><h5>Product Manager</h5></div>
    <form onSubmit={handleSubmit}>
        <p>
            <label>NAME :</label><br/>
            <input name="title" type="text" onChange={handleTitle} value={title} className={validationErrs.title? "form-control is-invalid" : "form-control"}/>
            {validationErrs.title? <p className='invalid-feedback'>{validationErrs.title}</p> : ' '}
        </p>


        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        
    </form>

    </>
  )
}

export default Form
