    const [name, setName] = useState(""); 
    const [isSubmited, setIsSubmited] = useState(false);


    const handleSubmit = e => {
        setIsSubmited(false)
        e.preventDefault();

        props.onSubmitHandler({name})
        setIsSubmited(true)
        setName("")
    }



    const handleTitle = (e) => {
        setName(e.target.value)
    }



  return (
    <>

