import React, { useState } from 'react'

const Form = (props) => {

    const [name, setName] = useState(""); 
    const [isSubmited, setIsSubmited] = useState(false);


    const handleSubmit = e => {
        setIsSubmited(false)
        e.preventDefault();
        props.onSubmitHandler({name})
        setIsSubmited(true)
        setName("")
    }



    const handleTitle = (e) => {
        setName(e.target.value)
    }



  return (
    <>
<div className='container'>

    
</div>
    <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
    <form onSubmit={handleSubmit}>

        <p>
            <label>NAME :</label><br/>
            <input name="name" type="text" onChange={handleTitle} value={name} className="form-control"/>
        </p>

        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        
    </form>

    </>
  )
}

export default Form
