import React, { useState } from 'react'
import { Link } from "react-router-dom";

const Form = (props) => {

    const [name, setName] = useState(props.initName); 
    // const [isSubmited, setIsSubmited] = useState(false);
    console.log(name)


    const handleSubmit = e => {
        // setIsSubmited(false)
        e.preventDefault();
        props.onSubmitHandler({name})
        // setIsSubmited(true)
        setName("")
    }



    const handleName = (e) => {
        setName(e.target.value)
    }



  return (
    <>
<div className='container mt-5'>
    {props.errors.map((errMsg , i) => <p key={i} className='alert alert-danger'>{errMsg} </p>)}
    {props.success && <p  className='alert alert-success'>your Favorite Author has been successafully add</p>}
    <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
    <form onSubmit={handleSubmit}>

        <p>
            <label>NAME :</label><br/>
            <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
        </p>

        <div className="d-flex justify-content-center">
        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        <Link to={props.linkRoute} className="btn btn-outline-secondary rounded-pill ms-2 "> Cancel </Link>
        </div>
        
    </form>

</div>

    </>
  )
}

export default Form
