import React, { useState , useEffect } from 'react'
import Form from '../components/Form'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";
// import { Link } from "react-router-dom";

const UpdatePage = (props) => {
    
    const {id} = useParams();
    const history = useHistory();
    const[errors , setErrors] = useState([])
    const[success , setSuccess] = useState(false)
    const [name, setName]  = useState('')
    const [author, setAuthor]  = useState('')


    useEffect(() => {
        axios.get('http://localhost:8000/api/author/'+id)
        .then(res => {
            setAuthor(res.data.Author)
            setName(res.data.Author.name); })
    }, []);


    
    // const updateProduct = e => {
    //     e.preventDefault();
    //     setIsSubmited(false)
    //     
    //     .then(res => {setIsSubmited(true)}) 
    //     .catch(err => console.error(err));
    // }


    const onSubmitHandler = (e) => {
        e.preventDefault();
        setSuccess(false)
        setErrors([])
        axios.put('http://localhost:8000/api/author/'+id , {name})
            .then(res=>{
                setSuccess(true)
                const authorName = author.name
                const val = e.target.value
                const newAuthor = ({...author, authorName : val})
                props.addToDom(newAuthor)
                history.push("/")
            })
            .catch(err=>{
                console.log(err)
                const data = err.response.data
                const errorMessages = [];
                if("errors" in data){
                    for(let field in data.errors){
                        const validationError = data.errors[field]
                        errorMessages.push(validationError.message)
                    }
                }
                setErrors(errorMessages)
            })
    }




    const handleName = (e) => {
        setName(e.target.value)
    }


  return (
    <>

    <div className='container mt-5'>
        {errors.map((errMsg , i) => <p key={i} className='alert alert-danger'>{errMsg} </p>)}
        {success && <p  className='alert alert-success'>your Favorite Author has been successafully updated</p>}
        <Link to="/"> HOME </Link>
        <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
        
        <div className='container mt-5  text-center'>
        <h5 className='text-primary'>we have quotes by :</h5>
        </div>

        <form onSubmit={onSubmitHandler}>

        <p>
            <label>NAME :</label><br/>
            <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
        </p>

        <div className="d-flex justify-content-center">
        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        <Link to={props.linkRoute} className="btn btn-outline-secondary rounded-pill ms-2 "> Cancel </Link>
        </div>
        
    </form>
    </div>

    </>
  )
}

export default UpdatePage
