return (
    <>

    <div className="text-center text-primary mb-5 mt-5"><h5>Favorite Authors</h5></div>
    <div className='container'><Link to='/new'>Add an Author</Link></div>
   
    <div className='container mt-5  text-center'>
    <h5 className='text-primary'>we have quotes by :</h5>
    <table className="table mt-3">

        <thead>
            <tr>
            <th scope="col">Name</th>
            <th scope="col">Type</th>
            <th scope="col">Actions</th>
            </tr>
        </thead>

        <tbody>
            {pets.map((author, idx) => 
            <tr key={idx}>
            <th scope="row">{author.name}</th>
            <th scope="row">{author.type}</th>
            <td> <Link className='btn btn-primary' to={'/edit/' + author._id}> Edit </Link>  |  <button className='btn btn-danger' onClick={(e) => {deleteHandler(author._id)}} > Delete </button> </td>
            </tr>
        )}
        </tbody>

    </table>
    </div>
    </>
)
}

export default HomePage
