import React from 'react'
import Table from '../components/Table'
import axios from 'axios';
const HomePage = ({authors, RemoveFromDom}) => {


    const deleteHandler = authorId => {
        axios.delete('http://localhost:8000/api/author/' + authorId)
        .then(res => RemoveFromDom(authorId))
        .catch(err => console.error(err));
    }


return (
    <>

    <div className='container mt-5'>
    <table className="table">

        <thead>
            <tr>
            <th scope="col">AUTHOR</th>
            <th scope="col">Action Available</th>
            </tr>
        </thead>

        <tbody>
            {authors.map((author, idx) => 
            <tr>
            <th scope="row">{author.name}</th>
            <td> <button onClick={(e) => {deleteHandler(author._id)}} > Edit </button>  |  <button> Delete </button> </td>
            </tr>
        )}
        </tbody>

    </table>
    </div>
    </>
)
}

export default HomePage
