import React, { useState } from 'react'
// import Form from '../components/Form'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";


const AddNewPage = (props) => {


    

    const {id} = useParams();
    const history = useHistory();
    const[errors , setErrors] = useState([])
    const[success , setSuccess] = useState(false)
    const [name, setName] = useState('')
    const [author , setAuthor]  = useState('')



    const onSubmitHandler = e => {
        e.preventDefault();
        setSuccess(false)
        setErrors([])
        axios.post('http://localhost:8000/api/author', {name})
            .then(res=>{
                setSuccess(true)
                // props.addToDom(newAuth)
                history.push("/")
            })
            .catch(err=>{
                const data = err.response.data
                const errorMessages = [];
                if("errors" in data){
                    for(let field in data.errors){
                        const validationError = data.errors[field]
                        errorMessages.push(validationError.message)
                    }
                }
                setErrors(errorMessages)
            })
    }




    const handleName = (e) => {
        setName(e.target.value)
    }





  return (
    <>
        <div className='container mt-5'>

        <Link to="/"> HOME </Link>
        <div className='container mt-5'>
        {errors.map((errMsg , i) => <p key={i} className='alert alert-danger'>{errMsg} </p>)}
        {success && <p  className='alert alert-success'> has been successafully Dss</p>}
        {/* <Link to="/"> HOME </Link> */}
        <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
        
        {/* <div className='container mt-5  text-center'> */}
        {/* <h5 className='text-primary'>we have quotes by :</h5> */}
        {/* </div> */}

        <form onSubmit={onSubmitHandler}>

        <div className='row'>
            <div className='col'>
            <p>
            <label>NAME :</label>
            <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
        </p>

        <p>
            <label>Type :</label>
            <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
        </p>

        <p>
            <label>Description :</label>
            <input name="name" type="text" onChange={handleName} value={name} className="form-control"/>
        </p>
            </div>


            <div className='col'></div>

        </div>

        <div className="d-flex justify-content-center">
        <div className="text-center"><input type="submit" className="btn btn-primary rounded-pill"/></div>
        <Link to="/" className="btn btn-outline-secondary rounded-pill ms-2 "> Cancel </Link>
        </div>
        
    </form>
    </div>

        </div>
    </>
  )
}

export default AddNewPage
