import React, { useState } from 'react'
// import Form from '../components/Form'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";
import Form from '../components/Form';


const AddNewPage = (props) => {


    

    const {id} = useParams();
    const history = useHistory();
    const[errors , setErrors] = useState([])
    const[success , setSuccess] = useState(false)




    const onSubmitHandler = (pet) => {
        setSuccess(false)
        setErrors([])
        axios.post('http://localhost:8000/api/pet', pet)
            .then(res=>{
                setSuccess(true)
                history.push("/")
            })
            .catch(err=>{
                const data = err.response.data
                const errorMessages = [];
                if("errors" in data){
                    for(let field in data.errors){
                        const validationError = data.errors[field]
                        errorMessages.push(validationError.message)
                    }
                }
                setErrors(errorMessages)
            })
    }










  return (
    <>
    <div>
        <div className='container mb-5 mt-5 pt-5 col-7 text-center'>

        {/* <Link to="/"> HOME </Link> */}
        <div className='container mt-5'>
        {errors.map((errMsg , i) => <p key={i} className='alert alert-danger'> {errMsg} </p>)}
        {success && <p  className='alert alert-success'> has been successafully Dss</p>}
        {/* <Link to="/"> HOME </Link> */}
        <div className="text-center text-primary "><h5>Favorite Authors</h5></div>

        {/* <div className='container mt-5  text-center'> */}
        {/* <h5 className='text-primary'>we have quotes by :</h5> */}
        {/* </div> */}

        <Form onSubmitHandler ={onSubmitHandler} />
        </div>

        </div>
    </div>
    </>
  )
}

export default AddNewPage
