import React, { useState , useEffect } from 'react'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";

const DetailsPage = () => {

    


        const {id} = useParams();
        const[pet, setPet] = useState('')
        

        useEffect(() => {
            axios.get('http://localhost:8000/api/pet/'+id)
            .then(res => setPet(res.data.pet))
            .catch(err => console.error(err))
        }, []);



  return (
    <>
    <div className='container mb-5 mt-5 pt-5 col-7'>
    <div className='form-border p-5'>

    <div className='d-flex justify-content-between mt-3 mb-5'>
        <div className='text-primary'><h5>Pet Shelter</h5></div>
        <div><Link to="/" className="btn btn-outline-secondary rounded-pill ms-2 ">Back to HOME</Link></div>
    </div>


        <h6 className='mb-3 fw-bold'>Details About : <span className='text-primary'>{pet.name}</span> </h6>\
        <hr></hr>

        <p><span className='fw-bold'>Pet Type : </span><span className='text-primary'>{pet.type}</span></p>

        <p><span className='fw-bold'>Description :</span> <span className='text-primary'>{pet.desc}</span></p>

        {/* <ul> */}
            {/* {pet.skill1 !== " " ? <p>{pet.skill1}</p> : " " } */}
           <div className='mt-2'>
            <p className='fw-bold'>Skills : </p>
           <p className='text-primary'>{pet.skill1}</p>
            <p className='text-primary'>{pet.skill2}</p>
            <p className='text-primary'>{pet.skill3}</p>
           </div>
        {/* </ul> */}


    </div>

    </div>

        

    </>
  )
}

export default DetailsPage
