import './App.css';
import AddNewPage from './pages/AddNewPage'
import HomePage from './pages/HomePage'
import axios from 'axios';

const App = () =>{

  useEffect(()=> {
    axios.get('http://localhost:8000/api/ProductManage')
    .then(res=> setProducts(res.data.ProductManage))
    .catch(err=> console.log("Error : " , err))
}, [])


    return (
      <>
        <BrowserRouter>
        <Switch>

          <Route path="/new">
            <AddNewPage/>
          </Route>

          <Route path="/edit/:id">
            <h1>EDIT ID</h1>
          </Route>

          <Route path="/">
            <HomePage/>
          </Route>

        </Switch>
        </BrowserRouter>
      </>
    );
}

export default App;
