import React, { Component } from 'react';
import { BrowserRouter ,Switch ,Route , Link} from "react-router-dom";
import './App.css';
import AddNewPage from './pages/AddNewPage'
import HomePage from './pages/AddNewPage'

class App extends Component {
  render() {
    return (
      <>
        <BrowserRouter>
        <Switch>

          <Route path="/new">
            <AddNewPage/>
          </Route>

          <Route path="/edit/:id">
            <h1>EDIT ID</h1>
          </Route>

          <Route path="/">
            <h1>TABLE</h1>
          </Route>

        </Switch>
        </BrowserRouter>
      </>
    );
  }
}

export default App;
