import React, { useState , useEffect } from 'react'
import { useHistory , useParams} from 'react-router'
import axios from 'axios';
import { Link } from "react-router-dom";
import Form from '../components/Form';
// import { Link } from "react-router-dom";

const UpdatePage = (props) => {
    
    const {id} = useParams();
    const history = useHistory();
    const[errors , setErrors] = useState([])
    const[success , setSuccess] = useState(false)
    // const[pet , setPet] = useState(false)
    

    
    const [name, setName] = useState('')
    const [type, setType] = useState('')
    const [desc, setDesc] = useState('')

    const [skill1, setSkill1] = useState('')
    const [skill2, setSkill2] = useState('')
    const [skill3, setSkill3] = useState('')


    useEffect(() => {
        axios.get('http://localhost:8000/api/pet/'+id)
        .then(res => {
            // setPet(res.data.pet)
            setName(res.data.pet.name)
            setType(res.data.pet.type)
            setDesc(res.data.pet.desc)
            setSkill1(res.data.pet.skill1)
            setSkill2(res.data.pet.skill2)
            setSkill3(res.data.pet.skill3)


            console.log(res.data.pet)

            })
    }, []);




    const onSubmitHandler = (e) => {
        // e.preventDefault();
        setSuccess(false)
        setErrors([])
        axios.put('http://localhost:8000/api/pet/'+id , {name, type , desc , skill1 , skill2 , skill3})
            .then(res=>{
                setSuccess(true)
                // history.push("/")
            })
            .catch(err=>{
                console.log(err)
                const data = err.response.data
                const errorMessages = [];
                if("errors" in data){
                    for(let field in data.errors){
                        const validationError = data.errors[field]
                        errorMessages.push(validationError.message)
                    }
                }
                setErrors(errorMessages)
            })
    }





    // const handleName = (e) => {
    //     setName(e.target.value)
    // }

    // const handleType = (e) => {
    //     setType(e.target.value)
    // }

    // const handleDesc = (e) => {
    //     setDesc(e.target.value)
    // }

    // // Skills Handlers 
    // const handleSkill1 = (e) => {
    //     setSkill1(e.target.value)
    // }

    // const handleSkill2 = (e) => {
    //     setSkill2(e.target.value)
    // }

    // const handleSkill3 = (e) => {
    //     setSkill3(e.target.value)
    // }



  return (
    <>

    <div className='container mt-5'>
        {errors.map((errMsg , i) => <p key={i} className='alert alert-danger'>{errMsg} </p>)}
        {success && <p  className='alert alert-success'>your Favorite Author has been successafully updated</p>}
        {/* <Link to="/"> HOME </Link> */}
        <div className="text-center text-primary "><h5>Favorite Authors</h5></div>
        
        <div className='container mt-5  text-center'>
        <h5 className='text-primary'>we have quotes by :</h5>
        </div>

        <Form onSubmitHandler ={onSubmitHandler} initName={name} initType={type} initDesk={desc} initSkill1={skill1} initSkill2={skill2} initSkill3={skill3}/>
    </div>

    </>
  )
}

export default UpdatePage
