import React, { useState, useEffect } from 'react'
import Table from '../components/Table'
import axios from 'axios';
import { Link } from "react-router-dom";
const HomePage = (props) => {


    const [pets ,setPets] = useState([])

    useEffect(()=> {
        axios.get('http://localhost:8000/api/pet')
        .then(res=> setPets(res.data.pets))
        .catch(err=> console.log("Error : " , err))
    }, [])
  
  
    

    
    // const addToDom = authorId => {
    //     setAuthors([...authors , authorId])
    // }


    const deleteHandler = petId => {
        axios.delete('http://localhost:8000/api/pet/' + petId)
        .then(res => {
            setPets(pets.filter(pet => pet._id !==  petId))
            console.log(petId)
        })
        .catch(err => console.error(err));
    }


return (
    <>
    <div className='form-border col-8'>

    <div className="text-center text-primary mb-5 mt-5"><h5>Pet Shelter</h5></div>
    <div className='container'><Link to='/new'>Add an Author</Link></div>
   
    <div className='container mt-5  text-center'>
    <h5 className='text-primary'>we have quotes by :</h5>
    <table className="table mt-3">

        <thead>
            <tr>
            <th scope="col">Name</th>
            <th scope="col">Type</th>
            <th scope="col">Actions</th>
            </tr>
        </thead>

        <tbody>
            {pets.map((pet, idx) => 
            <tr key={idx}>
            <th scope="row">{pet.name}</th>
            <th scope="row">{pet.type}</th>
            <td><Link className='btn btn-secondary' to={'/pets/' + pet._id}> Detaile </Link>  | <Link className='btn btn-primary' to={'/edit/' + pet._id}> Edit </Link>  |  <button className='btn btn-danger' onClick={(e) => {deleteHandler(pet._id)}} > Delete </button> </td>
            </tr>
        )}
        </tbody>

    </table>
    </div>



    </div>
    
    </>
)
}

export default HomePage
